import { useEffect, useState } from "react";
import { Link ,useNavigate } from 'react-router-dom';
import axios from "axios";
//import { toast } from 'react-toastify'
import '../../node_modules/bootstrap/dist/css/bootstrap.css';

function DoctorPrescription(){


  //  var [ prescription,setPrescription ] =useState({doc_id:"",p_id:"",objectArray:[{m_id:"",dosage:"",duration:"",qtantiy:""},{m_id:"",dosage:"",duration:"",qtantiy:""},{m_id:"",dosage:"",duration:"",qtantiy:""}]})
    
    const [formData, setFormData] = useState({
        doc_id: 0,
        p_id: 0,
        medicines: []
    });

    const addMedicine = () => {
        setFormData((prevData) => ({
            ...prevData,
            medicines: [
                ...prevData.medicines,
                {
                    m_id: prevData.medicines.length + 1,
                    dosage: 'Default Dosage',
                    duration: 'Default Duration',
                    quantity: 1
                }
            ]
        }));
    };


    
    var nav=useNavigate();

    var Submit=()=>{
        console.log("aa gaya added fun me")
        console.log(formData)
            axios({
            method: 'post',
            url: 'http://localhost:7070/prescription/add',
            data: formData
        })
        .then(function (response) {
            //console.log(response.data);
            nav("/doctor/profile");
            
        })
        .catch(function (error) {
            console.log(error);
            console.log(formData);
        })
    };

    const handleMedicineChange = (index, field, value) => {
        setFormData((prevData) => {
            const updatedMedicines = [...prevData.medicines];
            updatedMedicines[index][field] = value;
            return {
                ...prevData,
                medicines: updatedMedicines
            };
        });
    };


    return (
        <div>
            <h3>Medicines</h3>
            <ul>
                {formData.medicines.map((medicine, index) => (
                    <li key={medicine.m_id}>
                        <p>ID: {medicine.m_id}</p>
                        <input
                            type="text"
                            value={medicine.dosage}
                            onChange={(e) => handleMedicineChange(index, 'dosage', e.target.value)}
                            placeholder="Dosage"
                        />
                        <input
                            type="text"
                            value={medicine.duration}
                            onChange={(e) => handleMedicineChange(index, 'duration', e.target.value)}
                            placeholder="Duration"
                        />
                        <input
                            type="number"
                            value={medicine.quantity}
                            onChange={(e) => handleMedicineChange(index, 'quantity', parseInt(e.target.value))}
                            placeholder="Quantity"
                        />
                    </li>
                ))}
            </ul>
            <button onClick={addMedicine}>Add Medicine</button>
        </div>
    );
}

export default DoctorPrescription;
